import React, { useState, useEffect } from "react";
import "../styles/MentalHealth.css";

const MentalHealth = () => {
  const [mood, setMood] = useState("");
  const [timer, setTimer] = useState(0);
  const [quote, setQuote] = useState("");

  const handleMoodChange = (event) => {
    setMood(event.target.value);
  };

  const handleStartTimer = () => {
    // Set the timer to 2 minutes (120 seconds)
    setTimer(120);

    // Fetch a meditation quote
    fetch("https://api.quotable.io/random?tags=meditation")
      .then((response) => response.json())
      .then((data) => setQuote(data.content))
      .catch((error) => console.error("Error fetching quote:", error));
  };

  useEffect(() => {
    let interval;

    if (timer > 0) {
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
    } else {
      clearInterval(interval);
      setTimer(0);
    }

    return () => clearInterval(interval);
  }, [timer]);

  return (
    <div className="mental-health-container">
      <h2>Mental Health</h2>
      <div className="mood-tracking">
        <label>How are you feeling today?</label>
        <select value={mood} onChange={handleMoodChange}>
          <option value="">Select</option>
          <option value="happy">Happy</option>
          <option value="sad">Sad</option>
          <option value="calm">Calm</option>
        </select>
      </div>
      <div className="meditation-timer">
        <button onClick={handleStartTimer} disabled={timer > 0}>
          Start Meditation Timer (2 minutes)
        </button>
        {timer > 0 && <p>Time remaining: {timer} seconds</p>}
      </div>
      <div className="quote-container">
        {quote && (
          <>
            <h3>Meditation Quote:</h3>
            <p>{quote}</p>
          </>
        )}
      </div>
    </div>
  );
};

export default MentalHealth;
